'use strict';

var pathDrawer = new joint.ui.PathDrawer({
    target: document.getElementById('canvas'),
    pathAttributes: {
        'class': 'example-path',
        'fill': '#e5e5e5',
        'stroke': '#23272d',
        'stroke-width': 2
    }
});

document.addEventListener('keydown', function(evt) {
    if (evt.shiftKey) pathDrawer.options.snapRadius = 30;
}, false);

document.addEventListener('keyup', function() {
    pathDrawer.options.snapRadius = 0;
}, false);
